import React, { useState } from 'react';
import {
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  Stepper,
  Step,
  StepLabel,
  Grid,
  Checkbox,
  FormControlLabel,
  Card,
  CardContent
} from '@mui/material';
import { Save } from '@mui/icons-material';
import { toast } from 'react-toastify';

function CampaignBuilder() {
  const [activeStep, setActiveStep] = useState(0);
  const [campaign, setCampaign] = useState({
    name: '',
    description: '',
    channels: {
      email: false,
      twitter: false,
      facebook: false,
      instagram: false,
      linkedin: false,
      imessage: false
    },
    templates: {
      email: { subject: '', body: '' },
      social: { message: '' },
      imessage: { message: '' }
    },
    schedule: [],
    recipientLists: []
  });

  const steps = ['Campaign Info', 'Select Channels', 'Create Templates', 'Schedule', 'Review'];

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleSaveCampaign = () => {
    const campaigns = JSON.parse(localStorage.getItem('campaigns') || '[]');
    campaigns.push({ ...campaign, id: Date.now().toString() });
    localStorage.setItem('campaigns', JSON.stringify(campaigns));
    toast.success('Campaign saved successfully!');
  };

  const getStepContent = (step) => {
    switch (step) {
      case 0:
        return (
          <Box>
            <TextField
              fullWidth
              label="Campaign Name"
              value={campaign.name}
              onChange={(e) => setCampaign({ ...campaign, name: e.target.value })}
              margin="normal"
            />
            <TextField
              fullWidth
              label="Description"
              multiline
              rows={4}
              value={campaign.description}
              onChange={(e) => setCampaign({ ...campaign, description: e.target.value })}
              margin="normal"
            />
          </Box>
        );

      case 1:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>
              Select Distribution Channels
            </Typography>
            <Grid container spacing={2}>
              {Object.keys(campaign.channels).map((channel) => (
                <Grid item xs={12} sm={6} key={channel}>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={campaign.channels[channel]}
                        onChange={(e) => setCampaign({
                          ...campaign,
                          channels: {
                            ...campaign.channels,
                            [channel]: e.target.checked
                          }
                        })}
                      />
                    }
                    label={channel.charAt(0).toUpperCase() + channel.slice(1)}
                  />
                </Grid>
              ))}
            </Grid>
          </Box>
        );

      case 2:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>
              Create Message Templates
            </Typography>

            {campaign.channels.email && (
              <Card sx={{ mb: 2 }}>
                <CardContent>
                  <Typography variant="subtitle1" gutterBottom>
                    Email Template
                  </Typography>
                  <TextField
                    fullWidth
                    label="Subject"
                    value={campaign.templates.email.subject}
                    onChange={(e) => setCampaign({
                      ...campaign,
                      templates: {
                        ...campaign.templates,
                        email: {
                          ...campaign.templates.email,
                          subject: e.target.value
                        }
                      }
                    })}
                    margin="normal"
                  />
                  <TextField
                    fullWidth
                    label="Body"
                    multiline
                    rows={4}
                    value={campaign.templates.email.body}
                    onChange={(e) => setCampaign({
                      ...campaign,
                      templates: {
                        ...campaign.templates,
                        email: {
                          ...campaign.templates.email,
                          body: e.target.value
                        }
                      }
                    })}
                    margin="normal"
                  />
                </CardContent>
              </Card>
            )}

            {(campaign.channels.twitter || campaign.channels.facebook ||
              campaign.channels.instagram || campaign.channels.linkedin) && (
              <Card sx={{ mb: 2 }}>
                <CardContent>
                  <Typography variant="subtitle1" gutterBottom>
                    Social Media Template
                  </Typography>
                  <TextField
                    fullWidth
                    label="Message"
                    multiline
                    rows={4}
                    value={campaign.templates.social.message}
                    onChange={(e) => setCampaign({
                      ...campaign,
                      templates: {
                        ...campaign.templates,
                        social: { message: e.target.value }
                      }
                    })}
                    margin="normal"
                  />
                </CardContent>
              </Card>
            )}

            {campaign.channels.imessage && (
              <Card>
                <CardContent>
                  <Typography variant="subtitle1" gutterBottom>
                    iMessage Template
                  </Typography>
                  <TextField
                    fullWidth
                    label="Message"
                    multiline
                    rows={4}
                    value={campaign.templates.imessage.message}
                    onChange={(e) => setCampaign({
                      ...campaign,
                      templates: {
                        ...campaign.templates,
                        imessage: { message: e.target.value }
                      }
                    })}
                    margin="normal"
                  />
                </CardContent>
              </Card>
            )}
          </Box>
        );

      case 3:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>
              Schedule Campaign
            </Typography>
            <Typography variant="body2" color="textSecondary">
              Configure when and how often this campaign should run
            </Typography>
            <TextField
              fullWidth
              type="datetime-local"
              label="Start Date & Time"
              InputLabelProps={{ shrink: true }}
              margin="normal"
            />
            <FormControlLabel
              control={<Checkbox />}
              label="Make this a recurring campaign"
            />
          </Box>
        );

      case 4:
        return (
          <Box>
            <Typography variant="h6" gutterBottom>
              Review Campaign
            </Typography>
            <Card>
              <CardContent>
                <Typography variant="subtitle1" gutterBottom>
                  <strong>Name:</strong> {campaign.name}
                </Typography>
                <Typography variant="subtitle1" gutterBottom>
                  <strong>Description:</strong> {campaign.description}
                </Typography>
                <Typography variant="subtitle1" gutterBottom>
                  <strong>Channels:</strong> {
                    Object.keys(campaign.channels)
                      .filter(ch => campaign.channels[ch])
                      .join(', ')
                  }
                </Typography>
              </CardContent>
            </Card>
          </Box>
        );

      default:
        return 'Unknown step';
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Campaign Builder
      </Typography>

      <Paper sx={{ p: 3 }}>
        <Stepper activeStep={activeStep}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        <Box sx={{ mt: 3 }}>
          {getStepContent(activeStep)}
        </Box>

        <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
          <Button
            disabled={activeStep === 0}
            onClick={handleBack}
          >
            Back
          </Button>

          {activeStep === steps.length - 1 ? (
            <Button
              variant="contained"
              onClick={handleSaveCampaign}
              startIcon={<Save />}
            >
              Save Campaign
            </Button>
          ) : (
            <Button
              variant="contained"
              onClick={handleNext}
            >
              Next
            </Button>
          )}
        </Box>
      </Paper>
    </Box>
  );
}

export default CampaignBuilder;
