import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import {
  Paper,
  Typography,
  Box,
  TextField,
  Button,
  Checkbox,
  FormControlLabel,
  FormGroup,
  Chip,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  Grid,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Tabs,
  Tab
} from '@mui/material';
import {
  Send,
  Schedule as ScheduleIcon,
  Delete
} from '@mui/icons-material';
import { toast } from 'react-toastify';
import axios from 'axios';

function ScheduleManager({ uploadedFiles = [] }) {
  const [tabValue, setTabValue] = useState(0);
  const [scheduleTime, setScheduleTime] = useState(new Date());
  const [recurring, setRecurring] = useState(false);
  const [recurringPattern, setRecurringPattern] = useState('daily');
  const [selectedChannels, setSelectedChannels] = useState({
    email: false,
    twitter: false,
    facebook: false,
    instagram: false,
    linkedin: false,
    imessage: false
  });
  const [emailConfig, setEmailConfig] = useState({
    recipients: [],
    subject: '',
    message: ''
  });
  const [socialConfig, setSocialConfig] = useState({
    message: '',
    accounts: {}
  });
  const [imessageConfig, setImessageConfig] = useState({
    recipients: [],
    message: ''
  });
  const [scheduledTasks, setScheduledTasks] = useState([]);
  const [recipients, setRecipients] = useState([]);
  const [selectedRecipients, setSelectedRecipients] = useState([]);

  useEffect(() => {
    loadRecipients();
    fetchScheduledTasks();
  }, []);

  const loadRecipients = () => {
    const saved = localStorage.getItem('recipients');
    if (saved) {
      setRecipients(JSON.parse(saved));
    }
  };

  const fetchScheduledTasks = async () => {
    try {
      const response = await axios.get('/api/scheduled-tasks', {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });
      setScheduledTasks(response.data.tasks || []);
    } catch (error) {
      console.error('Failed to fetch scheduled tasks:', error);
    }
  };

  const handleSendNow = async () => {
    if (!validateConfiguration()) return;

    try {
      const promises = [];

      if (selectedChannels.email) {
        promises.push(
          axios.post('/api/send/email', {
            files: uploadedFiles,
            recipients: emailConfig.recipients,
            subject: emailConfig.subject,
            message: emailConfig.message
          }, {
            headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
          })
        );
      }

      if (selectedChannels.twitter || selectedChannels.facebook ||
          selectedChannels.instagram || selectedChannels.linkedin) {
        const platforms = Object.keys(selectedChannels)
          .filter(key => selectedChannels[key] && key !== 'email' && key !== 'imessage');

        promises.push(
          axios.post('/api/send/social', {
            files: uploadedFiles,
            platforms,
            message: socialConfig.message,
            accounts: socialConfig.accounts
          }, {
            headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
          })
        );
      }

      if (selectedChannels.imessage) {
        promises.push(
          axios.post('/api/send/imessage', {
            files: uploadedFiles,
            recipients: imessageConfig.recipients,
            message: imessageConfig.message
          }, {
            headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
          })
        );
      }

      await Promise.all(promises);
      toast.success('Distribution sent successfully!');
    } catch (error) {
      toast.error(`Failed to send: ${error.response?.data?.error || error.message}`);
    }
  };

  const handleSchedule = async () => {
    if (!validateConfiguration()) return;

    const task = {
      type: 'multi',
      data: {
        files: uploadedFiles,
        email: selectedChannels.email ? emailConfig : null,
        social: (selectedChannels.twitter || selectedChannels.facebook ||
                 selectedChannels.instagram || selectedChannels.linkedin) ? {
          platforms: Object.keys(selectedChannels)
            .filter(key => selectedChannels[key] && key !== 'email' && key !== 'imessage'),
          message: socialConfig.message,
          accounts: socialConfig.accounts
        } : null,
        imessage: selectedChannels.imessage ? imessageConfig : null
      }
    };

    try {
      await axios.post('/api/schedule', {
        task,
        scheduleTime,
        recurring: recurring ? {
          pattern: getCronPattern(recurringPattern),
          timezone: Intl.DateTimeFormat().resolvedOptions().timeZone
        } : null
      }, {
        headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
      });

      toast.success('Task scheduled successfully!');
      fetchScheduledTasks();
    } catch (error) {
      toast.error(`Failed to schedule: ${error.response?.data?.error || error.message}`);
    }
  };

  const validateConfiguration = () => {
    if (uploadedFiles.length === 0) {
      toast.error('Please upload files first');
      return false;
    }

    if (!Object.values(selectedChannels).some(v => v)) {
      toast.error('Please select at least one distribution channel');
      return false;
    }

    if (selectedChannels.email && emailConfig.recipients.length === 0) {
      toast.error('Please select email recipients');
      return false;
    }

    if (selectedChannels.imessage && imessageConfig.recipients.length === 0) {
      toast.error('Please select iMessage recipients');
      return false;
    }

    return true;
  };

  const getCronPattern = (pattern) => {
    switch (pattern) {
      case 'hourly': return '0 * * * *';
      case 'daily': return '0 0 * * *';
      case 'weekly': return '0 0 * * 0';
      case 'monthly': return '0 0 1 * *';
      default: return '0 0 * * *';
    }
  };

  const handleRecipientSelect = (recipientId) => {
    const recipient = recipients.find(r => r.id === recipientId);
    if (!recipient) return;

    if (recipient.email) {
      setEmailConfig({
        ...emailConfig,
        recipients: [...emailConfig.recipients, recipient.email]
      });
    }

    if (recipient.phone) {
      setImessageConfig({
        ...imessageConfig,
        recipients: [...imessageConfig.recipients, recipient.phone]
      });
    }

    setSelectedRecipients([...selectedRecipients, recipientId]);
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Schedule & Send
      </Typography>

      <Paper sx={{ mb: 3 }}>
        <Tabs value={tabValue} onChange={(e, v) => setTabValue(v)}>
          <Tab label="Send Now" icon={<Send />} />
          <Tab label="Schedule" icon={<ScheduleIcon />} />
        </Tabs>
      </Paper>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 3, mb: 3 }}>
            <Typography variant="h6" gutterBottom>
              Select Channels
            </Typography>
            <FormGroup>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={selectedChannels.email}
                    onChange={(e) => setSelectedChannels({
                      ...selectedChannels,
                      email: e.target.checked
                    })}
                  />
                }
                label="Email"
              />
              <FormControlLabel
                control={
                  <Checkbox
                    checked={selectedChannels.twitter}
                    onChange={(e) => setSelectedChannels({
                      ...selectedChannels,
                      twitter: e.target.checked
                    })}
                  />
                }
                label="Twitter"
              />
              <FormControlLabel
                control={
                  <Checkbox
                    checked={selectedChannels.facebook}
                    onChange={(e) => setSelectedChannels({
                      ...selectedChannels,
                      facebook: e.target.checked
                    })}
                  />
                }
                label="Facebook"
              />
              <FormControlLabel
                control={
                  <Checkbox
                    checked={selectedChannels.instagram}
                    onChange={(e) => setSelectedChannels({
                      ...selectedChannels,
                      instagram: e.target.checked
                    })}
                  />
                }
                label="Instagram"
              />
              <FormControlLabel
                control={
                  <Checkbox
                    checked={selectedChannels.linkedin}
                    onChange={(e) => setSelectedChannels({
                      ...selectedChannels,
                      linkedin: e.target.checked
                    })}
                  />
                }
                label="LinkedIn"
              />
              <FormControlLabel
                control={
                  <Checkbox
                    checked={selectedChannels.imessage}
                    onChange={(e) => setSelectedChannels({
                      ...selectedChannels,
                      imessage: e.target.checked
                    })}
                  />
                }
                label="iMessage"
              />
            </FormGroup>
          </Paper>

          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Recipients
            </Typography>
            <FormControl fullWidth sx={{ mb: 2 }}>
              <InputLabel>Select Recipients</InputLabel>
              <Select
                value=""
                onChange={(e) => handleRecipientSelect(e.target.value)}
                label="Select Recipients"
              >
                {recipients
                  .filter(r => !selectedRecipients.includes(r.id))
                  .map(recipient => (
                    <MenuItem key={recipient.id} value={recipient.id}>
                      {recipient.name} ({recipient.email || recipient.phone})
                    </MenuItem>
                  ))}
              </Select>
            </FormControl>

            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
              {selectedRecipients.map(id => {
                const recipient = recipients.find(r => r.id === id);
                return (
                  <Chip
                    key={id}
                    label={recipient?.name}
                    onDelete={() => {
                      setSelectedRecipients(selectedRecipients.filter(r => r !== id));
                      // Also remove from email and imessage configs
                    }}
                  />
                );
              })}
            </Box>
          </Paper>
        </Grid>

        <Grid item xs={12} md={6}>
          {selectedChannels.email && (
            <Paper sx={{ p: 3, mb: 3 }}>
              <Typography variant="h6" gutterBottom>
                Email Configuration
              </Typography>
              <TextField
                fullWidth
                label="Subject"
                value={emailConfig.subject}
                onChange={(e) => setEmailConfig({ ...emailConfig, subject: e.target.value })}
                margin="normal"
              />
              <TextField
                fullWidth
                label="Message"
                multiline
                rows={4}
                value={emailConfig.message}
                onChange={(e) => setEmailConfig({ ...emailConfig, message: e.target.value })}
                margin="normal"
              />
            </Paper>
          )}

          {(selectedChannels.twitter || selectedChannels.facebook ||
            selectedChannels.instagram || selectedChannels.linkedin) && (
            <Paper sx={{ p: 3, mb: 3 }}>
              <Typography variant="h6" gutterBottom>
                Social Media Configuration
              </Typography>
              <TextField
                fullWidth
                label="Message"
                multiline
                rows={4}
                value={socialConfig.message}
                onChange={(e) => setSocialConfig({ ...socialConfig, message: e.target.value })}
                margin="normal"
              />
            </Paper>
          )}

          {selectedChannels.imessage && (
            <Paper sx={{ p: 3, mb: 3 }}>
              <Typography variant="h6" gutterBottom>
                iMessage Configuration
              </Typography>
              <TextField
                fullWidth
                label="Message"
                multiline
                rows={4}
                value={imessageConfig.message}
                onChange={(e) => setImessageConfig({ ...imessageConfig, message: e.target.value })}
                margin="normal"
              />
            </Paper>
          )}

          {tabValue === 1 && (
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Schedule Settings
              </Typography>
              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" gutterBottom>
                  Schedule Time
                </Typography>
                <DatePicker
                  selected={scheduleTime}
                  onChange={setScheduleTime}
                  showTimeSelect
                  dateFormat="Pp"
                  minDate={new Date()}
                  customInput={<TextField fullWidth />}
                />
              </Box>

              <FormControlLabel
                control={
                  <Checkbox
                    checked={recurring}
                    onChange={(e) => setRecurring(e.target.checked)}
                  />
                }
                label="Make this a recurring task"
              />

              {recurring && (
                <FormControl fullWidth sx={{ mt: 2 }}>
                  <InputLabel>Recurring Pattern</InputLabel>
                  <Select
                    value={recurringPattern}
                    onChange={(e) => setRecurringPattern(e.target.value)}
                    label="Recurring Pattern"
                  >
                    <MenuItem value="hourly">Hourly</MenuItem>
                    <MenuItem value="daily">Daily</MenuItem>
                    <MenuItem value="weekly">Weekly</MenuItem>
                    <MenuItem value="monthly">Monthly</MenuItem>
                  </Select>
                </FormControl>
              )}
            </Paper>
          )}
        </Grid>
      </Grid>

      <Box sx={{ mt: 3, display: 'flex', gap: 2 }}>
        {tabValue === 0 ? (
          <Button
            variant="contained"
            size="large"
            onClick={handleSendNow}
            startIcon={<Send />}
          >
            Send Now
          </Button>
        ) : (
          <Button
            variant="contained"
            size="large"
            onClick={handleSchedule}
            startIcon={<ScheduleIcon />}
          >
            Schedule Task
          </Button>
        )}
      </Box>

      {scheduledTasks.length > 0 && (
        <Paper sx={{ p: 3, mt: 4 }}>
          <Typography variant="h6" gutterBottom>
            Scheduled Tasks
          </Typography>
          <List>
            {scheduledTasks.map((task) => (
              <ListItem key={task.id}>
                <ListItemText
                  primary={`${task.type} - ${new Date(task.scheduleTime).toLocaleString()}`}
                  secondary={`Status: ${task.status} ${task.recurring ? '(Recurring)' : ''}`}
                />
                <ListItemSecondaryAction>
                  <IconButton edge="end">
                    <Delete />
                  </IconButton>
                </ListItemSecondaryAction>
              </ListItem>
            ))}
          </List>
        </Paper>
      )}
    </Box>
  );
}

export default ScheduleManager;
