import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Box,
  Tabs,
  Tab,
  List,
  ListItem
} from '@mui/material';
import { Pets as PetsIcon } from '@mui/icons-material';
import { toast } from 'react-toastify';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';

const API_URL = process.env.REACT_APP_API_URL || '/api';

function ClientDashboard() {
  const [clientData, setClientData] = useState(null);
  const [media, setMedia] = useState([]);
  const [notes, setNotes] = useState([]);
  const [tabValue, setTabValue] = useState(0);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUserId(decoded.id);
      } catch (error) {
        console.error('Token decode error:', error);
      }
    }
  }, []);

  useEffect(() => {
    if (userId) {
      loadClientData();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId]);

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    return { headers: { Authorization: `Bearer ${token}` } };
  };

  const loadClientData = async () => {
    try {
      const response = await axios.get(`${API_URL}/clients/user/${userId}`, getAuthHeaders());
      const client = response.data.client;
      setClientData(client);

      // Load media and notes for this client
      if (client) {
        loadMedia(client.id);
        loadNotes(client.id);
      }
    } catch (error) {
      console.error('Failed to load client data:', error);
      toast.error('Failed to load your information');
    }
  };

  const loadMedia = async (clientId) => {
    try {
      const response = await axios.get(`${API_URL}/media/client/${clientId}`, getAuthHeaders());
      setMedia(response.data.media || []);
    } catch (error) {
      console.error('Failed to load media:', error);
    }
  };

  const loadNotes = async (clientId) => {
    try {
      const response = await axios.get(`${API_URL}/notes/client/${clientId}`, getAuthHeaders());
      setNotes(response.data.notes || []);
    } catch (error) {
      console.error('Failed to load notes:', error);
    }
  };

  if (!clientData) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Paper sx={{ p: 4, textAlign: 'center' }}>
          <Typography>Loading...</Typography>
        </Paper>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" gutterBottom>
        Welcome, {clientData.clientName}!
      </Typography>

      {/* Dog Information Card */}
      <Paper sx={{ p: 3, mb: 3 }}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={4}>
            {clientData.dogImage ? (
              <img
                src={clientData.dogImage}
                alt={clientData.dogName}
                style={{ width: '100%', borderRadius: '8px' }}
              />
            ) : (
              <Box
                sx={{
                  width: '100%',
                  height: 200,
                  backgroundColor: 'grey.200',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  borderRadius: '8px'
                }}
              >
                <PetsIcon sx={{ fontSize: 60, color: 'grey.400' }} />
              </Box>
            )}
          </Grid>
          <Grid item xs={12} md={8}>
            <Typography variant="h5" gutterBottom>
              {clientData.dogName}
            </Typography>
            <Typography variant="body1" gutterBottom>
              <strong>Breed:</strong> {clientData.dogBreed}
            </Typography>
            <Typography variant="body1" gutterBottom>
              <strong>Age:</strong> {clientData.dogAge}
            </Typography>
            {clientData.notes && (
              <Typography variant="body1" gutterBottom sx={{ mt: 2 }}>
                <strong>Additional Info:</strong> {clientData.notes}
              </Typography>
            )}
          </Grid>
        </Grid>
      </Paper>

      {/* Content Tabs */}
      <Paper sx={{ p: 2 }}>
        <Tabs value={tabValue} onChange={(e, newValue) => setTabValue(newValue)}>
          <Tab label={`Photos & Videos (${media.length})`} />
          <Tab label={`Notes (${notes.length})`} />
        </Tabs>

        {/* Media Tab */}
        {tabValue === 0 && (
          <Box sx={{ mt: 3 }}>
            {media.length === 0 ? (
              <Typography color="text.secondary" textAlign="center">
                No media available yet
              </Typography>
            ) : (
              <Grid container spacing={3}>
                {media.map((item) => (
                  <Grid item xs={12} sm={6} md={4} key={item.id}>
                    <Card>
                      {item.type === 'video' ? (
                        <video
                          src={item.url}
                          controls
                          style={{ width: '100%', height: '250px', objectFit: 'cover' }}
                        />
                      ) : (
                        <CardMedia
                          component="img"
                          height="250"
                          image={item.url}
                          alt={item.originalName}
                          sx={{ objectFit: 'cover' }}
                        />
                      )}
                      {item.caption && (
                        <CardContent>
                          <Typography variant="body2">{item.caption}</Typography>
                          <Typography variant="caption" color="text.secondary">
                            {new Date(item.createdAt).toLocaleDateString()}
                          </Typography>
                        </CardContent>
                      )}
                    </Card>
                  </Grid>
                ))}
              </Grid>
            )}
          </Box>
        )}

        {/* Notes Tab */}
        {tabValue === 1 && (
          <Box sx={{ mt: 3 }}>
            {notes.length === 0 ? (
              <Typography color="text.secondary" textAlign="center">
                No notes available yet
              </Typography>
            ) : (
              <List>
                {notes.map((note) => (
                  <ListItem key={note.id} sx={{ mb: 2 }}>
                    <Paper sx={{ p: 2, width: '100%' }}>
                      <Typography variant="h6" gutterBottom>
                        {note.title}
                      </Typography>
                      <Typography variant="body1" paragraph>
                        {note.content}
                      </Typography>
                      <Typography variant="caption" color="text.secondary">
                        {new Date(note.createdAt).toLocaleDateString()}
                      </Typography>
                    </Paper>
                  </ListItem>
                ))}
              </List>
            )}
          </Box>
        )}
      </Paper>
    </Container>
  );
}

export default ClientDashboard;
