import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Button,
  TextField,
  IconButton,
  List,
  ListItem,
  ListItemText,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Box,
  Card,
  CardContent,
  CardMedia,
  Tabs,
  Tab
} from '@mui/material';
import {
  Add as AddIcon,
  Delete as DeleteIcon,
  CloudUpload as UploadIcon,
  Person as PersonIcon
} from '@mui/icons-material';
import { toast } from 'react-toastify';
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || '/api';

function AdminDashboard() {
  const [clients, setClients] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);
  const [showClientDialog, setShowClientDialog] = useState(false);
  const [showMediaDialog, setShowMediaDialog] = useState(false);
  const [showNoteDialog, setShowNoteDialog] = useState(false);
  const [clientMedia, setClientMedia] = useState([]);
  const [clientNotes, setClientNotes] = useState([]);
  const [tabValue, setTabValue] = useState(0);

  const [clientForm, setClientForm] = useState({
    username: '',
    password: '',
    clientName: '',
    dogName: '',
    dogBreed: '',
    dogAge: '',
    dogImage: '',
    notes: ''
  });

  const [noteForm, setNoteForm] = useState({
    title: '',
    content: ''
  });

  const [mediaFile, setMediaFile] = useState(null);
  const [mediaCaption, setMediaCaption] = useState('');

  useEffect(() => {
    loadClients();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  useEffect(() => {
    if (selectedClient) {
      loadClientMedia(selectedClient.id);
      loadClientNotes(selectedClient.id);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedClient]);

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    return { headers: { Authorization: `Bearer ${token}` } };
  };

  const loadClients = async () => {
    try {
      const response = await axios.get(`${API_URL}/clients`, getAuthHeaders());
      setClients(response.data.clients || []);
    } catch (error) {
      toast.error('Failed to load clients');
    }
  };

  const loadClientMedia = async (clientId) => {
    try {
      const response = await axios.get(`${API_URL}/media/client/${clientId}`, getAuthHeaders());
      setClientMedia(response.data.media || []);
    } catch (error) {
      console.error('Failed to load media:', error);
    }
  };

  const loadClientNotes = async (clientId) => {
    try {
      const response = await axios.get(`${API_URL}/notes/client/${clientId}`, getAuthHeaders());
      setClientNotes(response.data.notes || []);
    } catch (error) {
      console.error('Failed to load notes:', error);
    }
  };

  const handleCreateClient = async () => {
    try {
      // First create the user
      const userResponse = await axios.post(
        `${API_URL}/auth/register`,
        {
          username: clientForm.username,
          password: clientForm.password,
          role: 'client'
        },
        getAuthHeaders()
      );

      // Then create the client profile
      await axios.post(
        `${API_URL}/clients`,
        {
          userId: userResponse.data.user.id,
          clientName: clientForm.clientName,
          dogName: clientForm.dogName,
          dogBreed: clientForm.dogBreed,
          dogAge: clientForm.dogAge,
          dogImage: clientForm.dogImage,
          notes: clientForm.notes
        },
        getAuthHeaders()
      );

      toast.success('Client created successfully');
      setShowClientDialog(false);
      setClientForm({
        username: '',
        password: '',
        clientName: '',
        dogName: '',
        dogBreed: '',
        dogAge: '',
        dogImage: '',
        notes: ''
      });
      loadClients();
    } catch (error) {
      toast.error(error.response?.data?.error || 'Failed to create client');
    }
  };

  const handleDeleteClient = async (userId) => {
    if (!window.confirm('Are you sure you want to delete this client?')) return;

    try {
      await axios.delete(`${API_URL}/clients/${userId}`, getAuthHeaders());
      toast.success('Client deleted successfully');
      setSelectedClient(null);
      loadClients();
    } catch (error) {
      toast.error('Failed to delete client');
    }
  };

  const handleUploadMedia = async () => {
    if (!mediaFile || !selectedClient) {
      toast.error('Please select a file');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('file', mediaFile);
      formData.append('clientId', selectedClient.id);
      formData.append('caption', mediaCaption);

      await axios.post(`${API_URL}/media/upload`, formData, {
        ...getAuthHeaders(),
        headers: {
          ...getAuthHeaders().headers,
          'Content-Type': 'multipart/form-data'
        }
      });

      toast.success('Media uploaded successfully');
      setShowMediaDialog(false);
      setMediaFile(null);
      setMediaCaption('');
      loadClientMedia(selectedClient.id);
    } catch (error) {
      toast.error('Failed to upload media');
    }
  };

  const handleCreateNote = async () => {
    if (!selectedClient || !noteForm.content) {
      toast.error('Please fill in all fields');
      return;
    }

    try {
      await axios.post(
        `${API_URL}/notes`,
        {
          clientId: selectedClient.id,
          title: noteForm.title,
          content: noteForm.content
        },
        getAuthHeaders()
      );

      toast.success('Note created successfully');
      setShowNoteDialog(false);
      setNoteForm({ title: '', content: '' });
      loadClientNotes(selectedClient.id);
    } catch (error) {
      toast.error('Failed to create note');
    }
  };

  const handleDeleteMedia = async (mediaId) => {
    if (!window.confirm('Delete this media?')) return;

    try {
      await axios.delete(`${API_URL}/media/${mediaId}`, getAuthHeaders());
      toast.success('Media deleted');
      loadClientMedia(selectedClient.id);
    } catch (error) {
      toast.error('Failed to delete media');
    }
  };

  const handleDeleteNote = async (noteId) => {
    if (!window.confirm('Delete this note?')) return;

    try {
      await axios.delete(`${API_URL}/notes/${noteId}`, getAuthHeaders());
      toast.success('Note deleted');
      loadClientNotes(selectedClient.id);
    } catch (error) {
      toast.error('Failed to delete note');
    }
  };

  return (
    <Container maxWidth="xl" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" gutterBottom>
        Admin Dashboard
      </Typography>

      <Grid container spacing={3}>
        {/* Client List */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 2 }}>
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
              <Typography variant="h6">Clients</Typography>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={() => setShowClientDialog(true)}
              >
                Add Client
              </Button>
            </Box>
            <List>
              {clients.map((client) => (
                <ListItem
                  key={client.id}
                  button
                  selected={selectedClient?.id === client.id}
                  onClick={() => setSelectedClient(client)}
                  secondaryAction={
                    <IconButton
                      edge="end"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteClient(client.userId);
                      }}
                    >
                      <DeleteIcon />
                    </IconButton>
                  }
                >
                  <ListItemText
                    primary={client.clientName}
                    secondary={`Dog: ${client.dogName} - ${client.username}`}
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>

        {/* Client Details */}
        <Grid item xs={12} md={8}>
          {selectedClient ? (
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                {selectedClient.clientName}
              </Typography>

              <Tabs value={tabValue} onChange={(e, newValue) => setTabValue(newValue)}>
                <Tab label="Details" />
                <Tab label="Media" />
                <Tab label="Notes" />
              </Tabs>

              {/* Details Tab */}
              {tabValue === 0 && (
                <Box sx={{ mt: 2 }}>
                  <Grid container spacing={2}>
                    <Grid item xs={12}>
                      {selectedClient.dogImage && (
                        <img
                          src={selectedClient.dogImage}
                          alt={selectedClient.dogName}
                          style={{ maxWidth: '200px', borderRadius: '8px' }}
                        />
                      )}
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <Typography><strong>Dog Name:</strong> {selectedClient.dogName}</Typography>
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <Typography><strong>Breed:</strong> {selectedClient.dogBreed}</Typography>
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <Typography><strong>Age:</strong> {selectedClient.dogAge}</Typography>
                    </Grid>
                    <Grid item xs={12}>
                      <Typography><strong>Notes:</strong> {selectedClient.notes}</Typography>
                    </Grid>
                  </Grid>
                </Box>
              )}

              {/* Media Tab */}
              {tabValue === 1 && (
                <Box sx={{ mt: 2 }}>
                  <Button
                    variant="contained"
                    startIcon={<UploadIcon />}
                    onClick={() => setShowMediaDialog(true)}
                    sx={{ mb: 2 }}
                  >
                    Upload Media
                  </Button>
                  <Grid container spacing={2}>
                    {clientMedia.map((media) => (
                      <Grid item xs={12} sm={6} md={4} key={media.id}>
                        <Card>
                          {media.type === 'video' ? (
                            <video
                              src={media.url}
                              controls
                              style={{ width: '100%', height: '200px' }}
                            />
                          ) : (
                            <CardMedia
                              component="img"
                              height="200"
                              image={media.url}
                              alt={media.originalName}
                            />
                          )}
                          <CardContent>
                            <Typography variant="body2">{media.caption}</Typography>
                            <IconButton
                              size="small"
                              onClick={() => handleDeleteMedia(media.id)}
                            >
                              <DeleteIcon />
                            </IconButton>
                          </CardContent>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                </Box>
              )}

              {/* Notes Tab */}
              {tabValue === 2 && (
                <Box sx={{ mt: 2 }}>
                  <Button
                    variant="contained"
                    startIcon={<AddIcon />}
                    onClick={() => setShowNoteDialog(true)}
                    sx={{ mb: 2 }}
                  >
                    Add Note
                  </Button>
                  <List>
                    {clientNotes.map((note) => (
                      <ListItem
                        key={note.id}
                        secondaryAction={
                          <IconButton edge="end" onClick={() => handleDeleteNote(note.id)}>
                            <DeleteIcon />
                          </IconButton>
                        }
                      >
                        <ListItemText
                          primary={note.title}
                          secondary={
                            <>
                              <Typography variant="body2">{note.content}</Typography>
                              <Typography variant="caption">
                                {new Date(note.createdAt).toLocaleDateString()}
                              </Typography>
                            </>
                          }
                        />
                      </ListItem>
                    ))}
                  </List>
                </Box>
              )}
            </Paper>
          ) : (
            <Paper sx={{ p: 4, textAlign: 'center' }}>
              <PersonIcon sx={{ fontSize: 60, color: 'text.secondary' }} />
              <Typography variant="h6" color="text.secondary">
                Select a client to view details
              </Typography>
            </Paper>
          )}
        </Grid>
      </Grid>

      {/* Create Client Dialog */}
      <Dialog open={showClientDialog} onClose={() => setShowClientDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Create New Client</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            margin="normal"
            label="Username"
            value={clientForm.username}
            onChange={(e) => setClientForm({ ...clientForm, username: e.target.value })}
          />
          <TextField
            fullWidth
            margin="normal"
            label="Password"
            type="password"
            value={clientForm.password}
            onChange={(e) => setClientForm({ ...clientForm, password: e.target.value })}
          />
          <TextField
            fullWidth
            margin="normal"
            label="Client Name"
            value={clientForm.clientName}
            onChange={(e) => setClientForm({ ...clientForm, clientName: e.target.value })}
          />
          <TextField
            fullWidth
            margin="normal"
            label="Dog Name"
            value={clientForm.dogName}
            onChange={(e) => setClientForm({ ...clientForm, dogName: e.target.value })}
          />
          <TextField
            fullWidth
            margin="normal"
            label="Dog Breed"
            value={clientForm.dogBreed}
            onChange={(e) => setClientForm({ ...clientForm, dogBreed: e.target.value })}
          />
          <TextField
            fullWidth
            margin="normal"
            label="Dog Age"
            value={clientForm.dogAge}
            onChange={(e) => setClientForm({ ...clientForm, dogAge: e.target.value })}
          />
          <TextField
            fullWidth
            margin="normal"
            label="Dog Image URL"
            value={clientForm.dogImage}
            onChange={(e) => setClientForm({ ...clientForm, dogImage: e.target.value })}
          />
          <TextField
            fullWidth
            margin="normal"
            label="Notes"
            multiline
            rows={3}
            value={clientForm.notes}
            onChange={(e) => setClientForm({ ...clientForm, notes: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowClientDialog(false)}>Cancel</Button>
          <Button onClick={handleCreateClient} variant="contained">Create</Button>
        </DialogActions>
      </Dialog>

      {/* Upload Media Dialog */}
      <Dialog open={showMediaDialog} onClose={() => setShowMediaDialog(false)}>
        <DialogTitle>Upload Media</DialogTitle>
        <DialogContent>
          <input
            type="file"
            accept="image/*,video/*"
            onChange={(e) => setMediaFile(e.target.files[0])}
            style={{ margin: '20px 0' }}
          />
          <TextField
            fullWidth
            label="Caption"
            value={mediaCaption}
            onChange={(e) => setMediaCaption(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowMediaDialog(false)}>Cancel</Button>
          <Button onClick={handleUploadMedia} variant="contained">Upload</Button>
        </DialogActions>
      </Dialog>

      {/* Create Note Dialog */}
      <Dialog open={showNoteDialog} onClose={() => setShowNoteDialog(false)}>
        <DialogTitle>Add Note</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            margin="normal"
            label="Title"
            value={noteForm.title}
            onChange={(e) => setNoteForm({ ...noteForm, title: e.target.value })}
          />
          <TextField
            fullWidth
            margin="normal"
            label="Content"
            multiline
            rows={4}
            value={noteForm.content}
            onChange={(e) => setNoteForm({ ...noteForm, content: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowNoteDialog(false)}>Cancel</Button>
          <Button onClick={handleCreateNote} variant="contained">Create</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default AdminDashboard;
